import React, { Component } from "react";
import PageHelmet from "../component/common/Helmet";
import Breadcrumb from "./common/Breadcrumb";
import { FiCast, FiLayers, FiUsers, FiMonitor, FiChevronUp } from "react-icons/fi";
import ScrollToTop from 'react-scroll-up';
import Header from "../component/header/HeaderFive";
import Footer from "../component/footer/Footer";
import CallAction from "./callaction/CallAction";
import axios from 'axios';

const ServiceList = [
    {
        icon: <FiCast />,
        title: 'Application Managed Services',
        description: 'I throw myself down among the tall grass by the stream as I lie close to the earth.'
    },
    {
        icon: <FiLayers />,
        title: 'Commercial /Procurement And Supplies ',
        description: 'I throw myself down among the tall grass by the stream as I lie close to the earth.'
    },
    {
        icon: <FiUsers />,
        title: 'Volunteering/Freelance Service',
        description: 'I throw myself down among the tall grass by the stream as I lie close to the earth.'
    },
    {
        icon: <FiMonitor />,
        title: 'SME Support Services',
        description: 'I throw myself down among the tall grass by the stream as I lie close to the earth.'
    },
    {
        icon: <FiCast />,
        title: 'Online Business Support Platform',
        description: 'I throw myself down among the tall grass by the stream as I lie close to the earth.'
    },

]
class Service extends Component {

    constructor(props) {
        super(props)
       
        this.state = {
           
          
            standard:'',
            standards:[]
           
            
           
           

        };
       
    }
   
    
    componentDidMount() {
        
        this.Standardservice();
        
      }
      
      Standardservice(){
        

        
      
        axios.get('http://localhost:8000/api/standardservices/'+ this.props.match.params.id).then(res => res.data)
        .then(res => {
            this.setState({
                standard: res.data.title,
                standards:res.data.services
            
            });
            console.log(this.state.standards);
          })
          .catch((error) => {
            console.log(error);
          })
        
    }
    render() {
        return (
            
            <React.Fragment>
                <PageHelmet pageTitle='Business Support Services' />
                {/* Start Header Area  */}
                <Header headerPosition="header--static logoresize" logo="all-dark" color="color-black" />
                {/* End Header Area  */}

                {/* Start Breadcrump Area */}
                <Breadcrumb title={this.state.standard} />
                {/* End Breadcrump Area */}

                {/* Start Service Area */}
              
                <div className="service-area ptb--120 bg_color--5">
               
                    <div className="container">
                  
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="section-title text-center mb--30">
                                    <h2>{this.state.standard}</h2>
                                    <p>There are many variations of passages of Lorem Ipsum available, <br /> but the majority have suffered alteration.</p>
                                </div>
                            </div>
                        </div>
                
                        {/* Start Columns Area  */}
                        <div className="rn-columns-area ptb--120 bg_color--1">
                            <div className="container">
                                <div className="row">
                                    <div className="col-lg-6 col-md-6 col-12">
                                        <div className="single-column">

                                            <p>There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum. You need to be sure there isn't anything embarrassing hidden in the middle of text. All the Lorem Ipsum generators on the Internet tend toitrrepeat.</p>
                                        </div>
                                    </div>
                                    <div className="col-lg-6 col-md-6 col-12 mt_sm--30">
                                        <div className="single-column">

                                            <p>There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum. You need to be sure there isn't anything embarrassing hidden in the middle of text. All the Lorem Ipsum generators on the Internet tend toitrrepeat.</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        {/* Start Columns Area  */}

                        <div className="row service-one-wrapper">
                            {this.state.standards.map(serve => 
                                <div className="col-xl-4 col-lg-4 col-md-6 col-sm-6 col-12" key={serve.id}>

                                    <div className="service service__style--2">
                                        <div className="icon">
                                            <icon>{serve.icon}</icon>
                                        </div>
                                        <div className="content">
                                            <h3 className="title">{serve.title}</h3>
                                            <p>{serve.description}</p>
                                            <a href="/service-details"><span className="text">Learn More</span></a>
                                        </div>
                                    </div>

                                </div>
                            )}
                        </div>
                    </div>
                </div>
                {/* End Service Area */}



                {/* Start Back To Top */}
                <div className="backto-top">
                    <ScrollToTop showUnder={160}>
                        <FiChevronUp />
                    </ScrollToTop>
                </div>
                {/* End Back To Top */}

                {/* Start call To Action  */}
                <CallAction />
                {/* End call To Action  */}



                {/* Start Footer Style  */}
                <Footer />
                {/* End Footer Style  */}


            </React.Fragment>
        )
    }
}
export default Service;
